mapper.select:
SELECT uf.*, f.food_name, f.calory
FROM user_food uf
JOIN food f ON uf.food_id = f.food_id
WHERE uf.user_id = #{userId}
AND YEAR(uf.date) = #{year}
AND MONTH(uf.date) = #{month}
ORDER BY uf.date DESC, uf.created_at DESC

mapper.update:
UPDATE user_food
SET food_id = #{foodId},
    date = #{date},
    quantity = #{quantity}
WHERE user_food_id = #{userFoodId}
